import React, { Component, useState } from "react";
import axios from "axios";
import TextInput from "components/TextInput";
//import Button from "components/Button";

import { autocompleteClasses, Button, TextField } from "@mui/material";
import ResultDisplay from "components/ResultDisplay";
import { ClassNames } from "@emotion/react";
import AppBar from "@mui/material/AppBar";
import Box from "@mui/material/Box";
import Container from "@mui/material/Container";
import Toolbar from "@mui/material/Toolbar";
import Typography from "@mui/material/Typography";
import CssBaseline from "@mui/material/CssBaseline";
import { Height } from "@mui/icons-material";

const MainPage: React.FC = () => {
  const [userInput, setUserInput] = useState("");
  const [result, setResult] = useState("");

  const handleSubmit = async () => {
    try {
      const response = await axios.post("/api/process", { message: userInput });
      setResult(response.data.result);
    } catch (error) {
      console.error("Error:", error);
    }
  };

  return (
    <div className="container-fluid">
      <AppBar>
        <Toolbar>
          <Typography variant="h6">My App</Typography>
        </Toolbar>
      </AppBar>
      <React.Fragment>
        <CssBaseline />
        <Container
          maxWidth={false}
          sx={{ width: "100%", bgcolor: "#f5f5f5", height: "100%" }}
        >
          <TextField
            id="outlined-basic"
            label="Enter your message"
            variant="outlined"
            onChange={(e) => setUserInput(e.target.value)}
            sx={{ width: "80%", hight: "40px", mt: 5, mb: 5, mr: 1 }}
          />

          <Button
            color="secondary"
            onClick={handleSubmit}
            variant="contained"
            sx={{ width: "200px", mt: 5, mb: 5, mr: 5 }}
          >
            Submit
          </Button>

          {/* Header Text */}
          <Typography variant="h4" component="h1" gutterBottom>
            Result
          </Typography>
          <ResultDisplay
            content={result}
            style={{
              backgroundColor: "#fcfcfc", // Background color
              width: "100%", // Width
              height: "200px", // Height
              border: "2px inset", // Border
              display: "flex", // Flexbox layout
              justifyContent: "left", // Center content horizontally
              alignItems: "left", // Center content vertically
              overflow: "auto",
            }}
          />

          {/* Description */}
          <Typography variant="body2" gutterBottom>
            This is a simple container example using Material UI. You can add
            any content here!
          </Typography>

          {/* Buttons */}
          <Box sx={{ display: "flex", gap: 2, marginTop: 2 }}>
            <Button variant="contained" color="primary">
              Action 1
            </Button>
            <Button variant="outlined" color="secondary">
              Action 2
            </Button>
          </Box>

          {/* Custom Box */}
          <Box
            sx={{
              bgcolor: "#cfe8fc",
              height: "200px",
              width: "100%",
              marginTop: 4,
              display: "flex",
              justifyContent: "center",
              alignItems: "center",
            }}
          >
            <Typography>Custom Box inside Container</Typography>
          </Box>
        </Container>
      </React.Fragment>
    </div>
  );
};

export default MainPage;
