import React from 'react'

interface ResultDisplayProps {

    content: string | JSX.Element
    title?: string
    style?: React.CSSProperties
}

const ResultDisplay: React.FC<ResultDisplayProps> = ({ title, content, style }) => {
    return (
        <div
            style={{
                border: '1px solid #ddd',
                borderRadius: '5px',
                padding: '15px',
                backgroundColor: '#f9f9f9',
                ...style,
            }}
        >
            {title && (
                <h2 style={{ margin: '0 0 10px 0', fontSize: '18px', color: '#333' }}>{title}</h2>
            )}
            {Array.isArray(content) ? (
                content.map((item, index) => <div key={index}>{index + 1}: {item}</div>)
            ) : (
            <div style={{ fontSize: '16px', color: '#555' }}>{content}</div>
            )}
        </div>
    )
}

export default ResultDisplay
