import React, { Component, useState } from "react";
import axios from "axios";
import TextInput from "components/TextInput";
//import Button from "components/Button";

import { autocompleteClasses, Button, TextField } from "@mui/material";
import ResultDisplay from "components/ResultDisplay";
import { ClassNames } from "@emotion/react";
import AppBar from "components/AppBar";
import Box from "@mui/material/Box";
import Container from "@mui/material/Container";
import Typography from "@mui/material/Typography";
import CssBaseline from "@mui/material/CssBaseline";
import { styled } from "@mui/material/styles";
import Paper from "@mui/material/Paper";
import Grid from "@mui/material/Grid2";

const MainPage: React.FC = () => {
  const [userInput, setUserInput] = useState("");
  const [result, setResult] = useState("");

  const handleSubmit = async () => {
    try {
      const response = await axios.post("/api/process", { message: userInput });
      setResult(response.data.result);
    } catch (error) {
      console.error("Error:", error);
    }
  };

  return (
    <div className="container-fluid">
      <AppBar></AppBar>
      <React.Fragment>
        <CssBaseline />
        <Container
          maxWidth={false}
          sx={{ width: "100%", bgcolor: "#f5f5f5", height: "100%" }}
        >
          <Grid container spacing={2}>
            <Grid size={10}>
              <TextField
                id="outlined-basic"
                label="Enter your message"
                variant="outlined"
                onChange={(e) => setUserInput(e.target.value)}
                sx={{ width: "100%", hight: "40px", mt: 5, mb: 5, mr: 1 }}
              />
            </Grid>
            <Grid size={2}>
              <Button
                color="primary"
                onClick={handleSubmit}
                variant="contained"
                sx={{ width: "100%", mt: 5, mb: 5, mr: 5 }}
              >
                Submit
              </Button>
            </Grid>
          </Grid>

          {/* Header Text */}
          <Typography variant="h4" component="h1" gutterBottom>
            Result
          </Typography>
          <ResultDisplay
            content={result}
            style={{
              backgroundColor: "#fcfcfc", // Background color
              width: "100%", // Width
              height: "200px", // Height
              border: "2px inset", // Border
              display: "flex", // Flexbox layout
              justifyContent: "left", // Center content horizontally
              alignItems: "left", // Center content vertically
              overflow: "auto",
            }}
          />

          {/* Description */}
          <Typography variant="body2" gutterBottom>
            This is a simple container example using Material UI. You can add
            any content here!
          </Typography>

          {/* Buttons */}
          <Box sx={{ display: "flex", gap: 2, marginTop: 2 }}>
            <Button variant="contained" color="primary">
              Action 1
            </Button>
            <Button variant="outlined" color="secondary">
              Action 2
            </Button>
          </Box>

          {/* Custom Box */}
          <Box
            sx={{
              bgcolor: "#cfe8fc",
              height: "200px",
              width: "100%",
              marginTop: 4,
              display: "flex",
              justifyContent: "center",
              alignItems: "center",
            }}
          >
            <Typography>Custom Box inside Container</Typography>
          </Box>
        </Container>
      </React.Fragment>
    </div>
  );
};

export default MainPage;
